package com.ailk.utils;import org.apache.commons.lang.StringEscapeUtils;public class Constructor {	private String name;	private String signature;	private String description;	public String getName() {		return name;	}	public void setName(String name) {		this.name = StringEscapeUtils.unescapeHtml(name).replace((char)(0xA0), ' ');	}	public String getSignature() {		return signature;	}	public void setSignature(String signature) {		this.signature = StringEscapeUtils.unescapeHtml(signature).replace((char)(0xA0), ' ');	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = StringEscapeUtils.unescapeHtml(description).replaceAll("<a href=\".*?>", "<a href=\"#\">").replace((char)(0xA0), ' ');	}	@Override	public String toString() {		return "Constructor [name=" + name + ", signature=" + signature + ", description=" + description + "]";	}}